import { createGlobalStyle } from 'styled-components';
import { Helmet } from 'react-helmet';

export const GlobalStyle = createGlobalStyle`
body {
  overflow-x: hidden;
  color: #232b2b;
  font-weight: 400;
  background-color: #f4f6f8;
  font-family: 'Roboto', sans-serif;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Roboto', sans-serif;
}

p,
a {
  font-family: 'Roboto', sans-serif;
}

a {
  text-decoration: none;
  transition: all 0.3s ease-in-out;
}

.wireframe {
  position: absolute;
  transform: rotate(45deg) translate(-11%, -32%);
}

.navbar {
  text-transform: uppercase;
  font-size: 0.6875rem;
  letter-spacing: 0.03125rem;
  display: flex;
  padding: 0.5rem 1rem;
  justify-content: space-between;
}

.navbar-custom {
  background: hsla(0, 0%, 100%, 0.99);
  z-index: 100;
  height: 5rem;
  border: 1px solid #ffdbce;
  border-top: 0;
  border-bottom-right-radius: 16.8px;
  border-bottom-left-radius: 16.8px;
}

.navbar-custom.shadow {
  box-shadow: 0 0.5rem 1rem rgba(255, 203, 170, 0.25) !important;
}

.navbar-custom .navbar-toggler-icon {
  background-image: url("data:image/svg+xml;charset=utf8,%3Csvg viewBox='0 0 32 32' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath stroke='rgba(244, 116, 75, 1)' stroke-width='2' stroke-linecap='round' stroke-miterlimit='10' d='M4 8h24M4 16h24M4 24h24'/%3E%3C/svg%3E");
}

.custom-toggler.navbar-toggler {
  border-color: #f4744b;
}

.navbar-collapse {
  background: hsla(0, 0%, 100%, 0.99);
  padding-left: 1.5rem;
}

.navbar-brand,
.footer-brand {
  color: rgb(66, 84, 102);
  font-weight: 700;
  font-size: 30px;
}

.footer-brand:hover,
.navbar-brand:hover {
  color: rgb(66, 84, 102);
}
.navbar-nav li {
  padding-right: 2rem;
}

`;

const BasicLayout = ({ children }: { children: any }) => {
  return (
    <>
      <Helmet>
        <link
          rel="stylesheet"
          href="https://fonts.googleapis.com/css2?family=Lalezar&display=swap"
        />
        <link
          rel="stylesheet"
          href="https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap"
        />
        <meta charSet="utf-8" />
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Helmet>
      <GlobalStyle />
      {children}
    </>
  );
};

export { BasicLayout };
