import styled from 'styled-components';

export const Header = styled.header`
  background: #fff;
`;

export const Navbar = styled.nav`
  text-transform: uppercase;
  font-size: 0.6875rem;
  letter-spacing: 0.03125rem;
  display: flex;
  padding: 0.5rem 1rem;
  justify-content: space-between;

  background: hsla(0, 0%, 100%, 0.99);
  z-index: 100;
  height: 5rem;
  border: 1px solid #ffdbce;
  border-top: 0;
  border-bottom-right-radius: 16.8px;
  border-bottom-left-radius: 16.8px;

  .shadow {
    box-shadow: 0 0.5rem 1rem rgba(255, 203, 170, 0.25) !important;
  }

  .navbar-toggler-icon {
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg viewBox='0 0 32 32' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath stroke='rgba(244, 116, 75, 1)' stroke-width='2' stroke-linecap='round' stroke-miterlimit='10' d='M4 8h24M4 16h24M4 24h24'/%3E%3C/svg%3E");
  }
`;

export const Container = styled.div``;

export const NavbarBrand = styled.a`
  color: rgb(66, 84, 102);
  font-weight: 700;
  font-size: 30px;

  :hover {
    color: rgb(66, 84, 102);
  }
`;

export const NavbarLogo = styled.img`
  width: 2.5rem;
`;

export const TogglerButton = styled.button`
  .navbar-toggler {
    border-color: #f4744b;
  }
`;

export const TogglerIcon = styled.span;

export const NavbarCollapse = styled.div;

export const NavbarListHolder = styled.ul;
